import pygame, randomclass Bird(pygame.sprite.Sprite):    def __init__(self, *screen):        super().__init__()        self.image = pygame.image.load('data/bluebird-midflap.png')        self.image = pygame.transform.scale2x(self.image)        self.screen = screen[0]        self.rect = self.image.get_rect()        self.rect.x = 100        self.rect.y = 100        self.screen.blit(self.image, (self.rect.x, self.rect.y))        self.count = 0        self.number = random.randint(0, 2)        self.bird_rect = self.image.get_rect(center=(self.rect.x, self.rect.y))    def update(self, *args):        self.bird_rect = self.image.get_rect(center=(self.rect.x, self.rect.y))        if self.rect.y <= 10:            print(1)            self.rect.y += 7        if self.rect.y >= 880:            self.count = 1            self.rect.y += 0        else:            if args and args[0].type == pygame.KEYDOWN:                if args[0].key == pygame.K_UP:                    if self.number == 0:                        self.image = pygame.image.load('data/bluebird-upflap.png')                        self.image = pygame.transform.scale2x(self.image)                        self.rect.y -= 10                    elif self.number == 1:                        self.image = pygame.image.load('data/redbird-upflap.png')                        self.image = pygame.transform.scale2x(self.image)                        self.rect.y -= 10                    else:                        self.image = pygame.image.load('data/yellowbird-upflap.png')                        self.image = pygame.transform.scale2x(self.image)                        self.rect.y -= 10            else:                if self.number == 0:                    self.image = pygame.image.load('data/bluebird-downflap.png')                    self.image = pygame.transform.scale2x(self.image)                    self.rect.y += 7                elif self.number == 1:                    self.image = pygame.image.load('data/redbird-downflap.png')                    self.image = pygame.transform.scale2x(self.image)                    self.rect.y += 7                else:                    self.image = pygame.image.load('data/yellowbird-downflap.png')                    self.image = pygame.transform.scale2x(self.image)                    self.rect.y += 7        self.screen.blit(self.image, (self.rect.x, self.rect.y))