import pygameclass GameOver(pygame.sprite.Sprite):    def __init__(self, screen):        self.screen = screen        self.image = pygame.image.load('data/message.png')        self.image = pygame.transform.scale2x(self.image)        self.game_font = pygame.font.Font('data/FONT.TTF', 30)        self.score = 0        self.vote = pygame.mixer.Sound('data/Game_Over.wav')        self.score_max = 0    def update(self):        self.screen.blit(self.image, (100, 256))    def game_over(self):        self.vote.play()    def delete_image(self):        self.screen.fill((255, 255, 255))    def score_up(self):        score_surface = self.game_font.render(f'Score: {int(self.score)}', True, (255, 255, 255))        score_rect = score_surface.get_rect(center=(300, 70))        high_score_surface = self.game_font.render(f'High Score: {int(self.score_max)}', True, (255, 255, 255))        high_score_rect = high_score_surface.get_rect(center=(120, 20))        self.screen.blit(score_surface, score_rect)        self.screen.blit(high_score_surface, high_score_rect)