import pygamefrom Bird import Birdclass Ground(pygame.sprite.Sprite):    def __init__(self, screen):        super().__init__()        self.screen = screen        self.image_ground = pygame.image.load('data/ground.png').convert()        self.image_ground = pygame.transform.scale2x(self.image_ground)        self.floor_x_pos = 0    def draw_ground(self):        self.screen.blit(self.image_ground, (self.floor_x_pos, 900))        self.screen.blit(self.image_ground, (self.floor_x_pos + 576, 900))        self.floor_x_pos -= 2        if self.floor_x_pos <= -576:            self.floor_x_pos = 0        # if bird.rect.y == 880:        # self.floor_x_pos = 0